INSERT INTO rex_template VALUES ('2','','02.01 - Produktnavi','<div id=produktuebersicht>Produktübersicht</div><?php\r\n\r\n// Pfad auslesen und als Array speichern\r\n$PATH = explode(\"|\",$this->getValue(\"path\").$this->getValue(\"article_id\").\"|\");\r\n$path1 = $PATH[1];\r\n$path2 = $PATH[2];\r\n$path3 = $PATH[3];\r\n$path4 = $PATH[4];\r\n$path5 = $PATH[5];\r\n$path6 = $PATH[6];\r\n\r\n// erst ebene ul starten\r\necho \'<ul class=\"produl\">\';\r\n\r\n/* START 1st level categories */\r\n\r\nforeach (OOCategory::getChildrenById(13) as $lev1):\r\n	\r\n	$lev1->setClang($REX[CUR_CLANG]);\r\n	\r\n	if ($lev1->isOnline()):\r\n	\r\n		if ($lev1->getId() == $path2)\r\n		{\r\n			/* 1st level - active link */\r\n			echo \'<li class=\"prodlia\"><a href=\"\'.$lev1->getUrl().\'\">\'.$lev1->getName().\'</a>\';\r\n		}else\r\n		{\r\n			/* 1st level - no active link */\r\n			echo \'<li class=\"prodlip\"><a href=\"\'.$lev1->getUrl().\'\">\'.$lev1->getName().\'</a>\';\r\n		}\r\n		\r\n		/* 1st level had categories? -> go on */\r\n		$lev1Size = sizeof($lev1->getChildren());\r\n		\r\n		if($lev1Size != \"0\" && $lev1->getId() == $path2): \r\n\r\n			// echo \'<ul class=\"nav2nd\">\';\r\n\r\n			/* START 2nd level categories */\r\n			foreach ($lev1->getChildren() as $lev2):\r\n\r\n				if ($lev2->isOnline()):\r\n	\r\n					if ($lev2->getId() == $path3)\r\n					{\r\n						/* 2nd level - active link */\r\n						echo \'<li class=\"prodlia2\"><a href=\"\'.$lev2->getUrl().\'\">\'.$lev2->getName().\'</a>\';\r\n					}else\r\n					{\r\n						/* 2nd level - no active link */\r\n						echo \'<li class=\"prodlip2\"><a href=\"\'.$lev2->getUrl().\'\">\'.$lev2->getName().\'</a>\';\r\n					}\r\n					\r\n					/* 2nd level had categories? -> go on */\r\n					$lev2Size = sizeof($lev2->getChildren());\r\n			\r\n					if($lev2Size != \"0\" && $lev2->getId() == $path3):\r\n						// echo \'<ul class=\"nav3rd\">\';\r\n					\r\n						/* START 3rd level categories */\r\n						foreach ($lev2->getChildren() as $lev3):\r\n							if ($lev3->isOnline()):\r\n								if ($lev3->getId() == $path4)\r\n								{\r\n									/* 3rd level - active link */\r\n									echo \'<li class=\"prodlia3\"><a href=\"\'.$lev3->getUrl().\'\">\'.$lev3->getName().\'</a></li>\';\r\n								}else\r\n								{\r\n									/* 3rd level - no active link */\r\n									echo \'<li class=\"prodlip3\"><a href=\"\'.$lev3->getUrl().\'\">\'.$lev3->getName().\'</a></li>\';\r\n								}\r\n							endif;\r\n						endforeach;\r\n						/* END 3rd level categories */\r\n			\r\n						// echo \'</ul>\';\r\n					endif;\r\n\r\n					echo \'</li>\';\r\n				endif;\r\n			\r\n			endforeach;\r\n			/* END 2nd level categories */\r\n			\r\n			// echo \'</ul>\';\r\n		endif;\r\n\r\n		echo \'</li>\';\r\n	endif;\r\nendforeach;\r\n/* END 1st level categories */\r\n\r\necho \'</ul>\';\r\n?><br>','','0','20050628125903','','','0','0');
INSERT INTO rex_template VALUES ('6','','02.02 - Top Navi','<img src=<? echo $REX[HTDOCS_PATH] ?>pics/leer.gif width=445 height=85><br><?php\r\n\r\n$PATH = explode(\"|\",$this->getValue(\"path\").$this->getValue(\"article_id\").\"|\");\r\n$path1 = $PATH[1];\r\n\r\necho \'<div class=topnavi>\';\r\nforeach (OOCategory::getRootCategories() as $lev1):\r\n	$lev1->setClang($REX[CUR_CLANG]);\r\n	if ($lev1->isOnline()):\r\n		if ($lev1->getId() == $path1)\r\n		{\r\n			echo \'<a href=\"\'.$lev1->getUrl().\'\" class=linktopa>\'.$lev1->getName().\'</a>\';\r\n		}else\r\n		{\r\n			echo \'<a href=\"\'.$lev1->getUrl().\'\" class=linktopp>\'.$lev1->getName().\'</a>\';\r\n		}\r\n		echo \'\';\r\n	endif;\r\nendforeach;\r\necho \'</div>\';\r\n\r\n?>','','0','20050725231743','','','0','0');
INSERT INTO rex_template VALUES ('7','','04.01 - Warenkorb','<?php\r\n\r\n$anzahl	= 0;\r\n$sum    = 0;\r\n\r\nif($FORM[\'ADDTOWK\'][\'id\']!= \"\"){\r\n\r\n	$_SESSION[\'REX_WK\'][\'id\'][] = $FORM[\'ADDTOWK\'][\'id\'];\r\n	$_SESSION[\'REX_WK\'][\'price\'][] = $FORM[\'ADDTOWK\'][\'price\'];\r\n\r\n}\r\n\r\nif($FORM[\'SUBFROMWK\'][\'id\']!= \"\"){\r\n\r\n	if(is_array($_SESSION[\'REX_WK\'][\'id\'])){\r\n		$hit = \"\";\r\n\r\n		$hit = array_search($FORM[\'SUBFROMWK\'][\'id\'], $_SESSION[\'REX_WK\'][\'id\']);\r\n\r\n		if($hit > -1){\r\n			unset ($_SESSION[\'REX_WK\'][\'id\'][$hit]);\r\n			unset ($_SESSION[\'REX_WK\'][\'price\'][$hit]);\r\n  		}\r\n\r\n 	}\r\n\r\n}\r\n\r\n\r\nif(is_array($_SESSION[\'REX_WK\'])){\r\n\r\n	for($i=0; $i<count($_SESSION[\'REX_WK\'][\'id\']);$i++ ){\r\n\r\n		$sum += current($_SESSION[\'REX_WK\'][\'price\']);\r\n		$anzahl++;\r\n		next($_SESSION[\'REX_WK\'][\'price\']);\r\n\r\n	}\r\n\r\n}\r\n\r\nif($this->getValue(\"article_id\") != 14 && $this->getValue(\"article_id\") != 46):\r\n	echo \"\r\n	<div class=warenkorbhl>Warenkorb</div>\r\n	<div class=warenkorbinhalt>bereits im Warenkorb: \".$anzahl.\" Produkte</div>\r\n	<div class=warenkorbsumme>Summe: \".number_format($sum,2,\".\",\"\").\" EUR</div>\r\n	<div class=warenkorbeinsehen><a href=\".rex_getUrl(14).\">Warenkorb einsehen</a></div>\";\r\n\r\n	//echo var_dump($_SESSION[\'REX_WK\']);\r\n	\r\nendif;\r\n?>\r\n','','0','20050630175113','','','0','0');
