INSERT INTO rex_modultyp VALUES ('3','','01.01 - Board','0','<?\r\n\r\n$board = new board;\r\n$board->setDB(1);\r\n$board->setTable(\"rex__board\");\r\n$board->setDocname(\"index.php\");\r\n$board->addLink(\"article_id\",REX_ARTICLE_ID);\r\n$board->setBoardname(\"REX_VALUE[1]\");\r\n$board->setRealBoardname(\"REX_VALUE[2]\");\r\n$board->anonymous = true;\r\n\r\necho $board->showBoard();\r\n\r\n?>','','Bitte gib die Boardbezeichnung ein:<br>\r\n<input type=text size=50 name=VALUE[2] value=\"REX_VALUE[2]\">\r\n\r\n<br><br>Bitte gib den Boardname für die Datenbank ein:<br>\r\n<input type=text size=50 name=VALUE[1] value=\"REX_VALUE[1]\">\r\n\r\n<br><br>','','0','0','','','','0','0');
INSERT INTO rex_modultyp VALUES ('11','','03.03 - Kategorienliste','0','<?\r\n\r\n$GC = new sql;\r\n// $GC->debugsql = 1;\r\n$GC->setQuery(\"select * from rex_category,rex_article \r\nwhere \r\nrex_category.re_category_id=\'REX_CATEGORY_ID\' and \r\nrex_article.startpage=1 and \r\nrex_article.category_id=rex_category.id  \r\norder by rex_article.prior,rex_article.name\");\r\n\r\n\r\nfor ($i=0;$i<$GC->getRows();$i++)\r\n{\r\n\r\n if ($i!=0)  echo \"<img src=$REX[HTDOCS_PATH]/pics/lgrey.gif width=450 height=1 vspace=10>\";\r\n\r\n if ($GC->getValue(\"rex_article.file\") != \"\") $file = \"<img src=$REX[HTDOCS_PATH]/files/\".$GC->getValue(\"rex_article.file\").\" width=155 height=90>\";\r\n else $file = \"<img src=$REX[HTDOCS_PATH]/files/kdummy.gif width=155 height=90 border=0>\";\r\n\r\n // $file = \"&nbsp;\";\r\n \r\n echo \"<div class=Categorylist>\";\r\n echo \"<div class=CategorylistPic>$file</div>\";\r\n echo \"<div class=CategorylistArticle>\";\r\n\r\n echo \"<div class=CategorylistArticleName>\".htmlentities($GC->getValue(\"rex_article.name\")).\"</div>\r\n\".nl2br(htmlentities($GC->getValue(\"rex_article.beschreibung\"))).\"<br>&raquo;&nbsp;<a href=index.php?article_id=\".$GC->getValue(\"rex_article.id\").\">Detailinformationen</a>\";\r\n\r\n echo \"</div>\";\r\n echo \"</div>\";\r\n\r\n\r\n $GC->next();\r\n}\r\n\r\n?>','','','','0','0','','','','0','0');
INSERT INTO rex_modultyp VALUES ('5','','02.02 - Text [WYSIWYG]','0','<table width=\"100%\" cellpadding=0 cellspacing=0 border=0>\r\n<tr>\r\n\r\n<td><font class=content>REX_HTML_VALUE[1]</font></td>\r\n\r\n</tr></table><?php\r\n\r\nif (\"REX_VALUE[9]\" == \"1\") echo \"<br>\";\r\nif (\"REX_VALUE[9]\" == \"2\") echo \"<br><br>\";\r\n\r\n?>','','Bitte gib den Text ein der zu Sehen sein soll:<br>\r\n<?php\r\n\r\n$a = new rex_wysiwyg_editor();\r\n$a->buttonrow1 = \"styleselect,separator,bold,italic,separator,bullist,numlist\";\r\n$a->buttonrow2 = \"link,linkHack,unlink,insertEmail,separator,removeformat,pasteRichtext,code\";\r\n$a->buttonrow3 = \"tablecontrols, separator, visualaid\";\r\n$a->buttonrow4 = \"rowseparator,formatselect,fontselect,fontsizeselect,forecolor,charmap\";\r\n$a->stylesheet = \"/redaxo3_0/files/text.css\";\r\n$a->content = \"REX_VALUE[1]\";\r\n$a->id = 1;\r\n$a->show();\r\n\r\n// mit umbruch ?\r\necho \"<br><br><select name=VALUE[9] size=1><option value=\'1\'\";\r\nif (\"REX_VALUE[9]\" == \"1\") echo \" selected\";\r\necho \">Mit einfachem Umbruch</option><option value=\'2\'\";\r\nif (\"REX_VALUE[9]\" == \"2\") echo \" selected\";\r\necho \">Mit doppeltem Umbruch</option><option value=\'0\'\";\r\nif (\"REX_VALUE[9]\" == \"0\" or \"REX_VALUE[9]\" == \"\") echo \" selected\";\r\necho \">Ohne Umbruch</option></select>\";\r\n\r\n?>','','0','0','','','','0','0');
INSERT INTO rex_modultyp VALUES ('6','','02.01 - Headline','0','<div class=Headline>REX_VALUE[1]</div>','','Headline:\r\n<br><input type=text size=30 name=VALUE[1] value=\"REX_VALUE[1]\" class=inp100>\r\n<br><br>\r\n\r\n','','0','0','','','','0','0');
INSERT INTO rex_modultyp VALUES ('8','','03.01 - Artikelliste','0','<div class=ArticlelistContainer>\r\n<?\r\n\r\nif (\"REX_VALUE[1]\" == 2) $sql = \"select * from rex_article where startpage=0 and checkbox01=1 and status=1 order by rex_article.prior\";\r\nelse if (\"REX_VALUE[1]\" == 1) $sql = \"select * from rex_article where rex_article.category_id=\'REX_CATEGORY_ID\' and startpage=0 and checkbox01=1 and status=1 order by rex_article.prior\";\r\nelse $sql = \"select * from rex_article where rex_article.category_id=\'REX_CATEGORY_ID\' and startpage=0 and status=1 order by rex_article.prior\";\r\n\r\n$GC = new sql;\r\n\r\n// $GC->setQuery(\"select * from rex_article where rex_article.category_id=\'REX_CATEGORY_ID\' and startpage=0 and status=1 order by rex_article.prior\");\r\n$GC->setQuery($sql);\r\n\r\nfor ($i=0;$i<$GC->getRows();$i++)\r\n{\r\n\r\n if ($i!=0)  echo \"<!--\r\n 	\r\n 	<hr style=\'width:100%; height:1px;\' vspace=4> -->\";\r\n\r\n $aid = $GC->getValue(\"rex_article.id\");\r\n\r\n $jahr = substr($GC->getValue(\"rex_article.online_von\"),0,4);\r\n $monat = substr($GC->getValue(\"rex_article.online_von\"),4,2);\r\n $tag = substr($GC->getValue(\"rex_article.online_von\"),6,2);\r\n\r\n $date = \"$tag.$monat.$jahr\";\r\n\r\n echo \"<div class=ArticlelistArticle>\";\r\n echo \"<div class=ArticlelistHead>\";\r\n echo \"	<div class=ArticlelistHeadline>\".htmlentities($GC->getValue(\"rex_article.name\")).\"</div>\";\r\n echo \"	<div class=ArticlelistDate>\".htmlentities($date).\"</div>\";\r\n echo \"</div>\";\r\n echo \"<div>\r\n		<div class=ArticlelistIntro>\";\r\n\r\n if ($GC->getValue(\"rex_article.file\")!=\"\") echo \"\r\n		<img src=<?=$REX[HTDOCS_PATH]?>/files/\".$GC->getValue(\"rex_article.file\").\" align=left style=\'border-right:10px #ffffff solid; border-bottom:10px #ffffff solid;\'>\";\r\n\r\n echo htmlentities($GC->getValue(\"rex_article.beschreibung\")).\" \";\r\n\r\n if ($REX[GG]) echo \"<a href=index.php?article_id=$aid><b>&raquo;&nbsp;mehr...</b></a>\";\r\n echo \"	</div></div>\";\r\n echo \"</div>\";\r\n\r\n $GC->next();\r\n}\r\n\r\n?>\r\n</div>','','<?\r\n\r\necho \"<br><br>Folgende Artikel sollen angezeigt werden:<br>\";\r\n\r\n$format = new select;\r\n$format->set_name(\"VALUE[1]\");\r\n$format->set_size(1);\r\n$format->set_style(\"\' class=\'inp100\");\r\n\r\n$format->add_option(\"Alle Artikel aus dieser Kategorie\",0);\r\n$format->add_option(\"Alle Artikel aus dieser Kategorie die als Teaser gekennzeichnet sind\",1);\r\n$format->add_option(\"Alle Artikel die als Teaser gekennzeichnet sind\",2);\r\n$format->set_selected(\"REX_VALUE[1]\");\r\necho $format->out();\r\n\r\n?><br><br>','','0','0','','','','0','0');
INSERT INTO rex_modultyp VALUES ('9','','03.02 - Artikeliste als Kalender','0','<?\r\n\r\n$category_id = $this->getValue(\"category_id\");\r\n$path  = $this->getValue(\"path\");\r\npreg_match_all(\"/-([0-9]+)/i\",$path,$match);\r\n$spath = $match[0][0];\r\n\r\n$GC = new sql;\r\n// $GC->debugsql = 1;\r\n$GC->setQuery(\"select * from rex_article \r\nwhere \r\n(rex_article.path like \'$spath-%\' or rex_article.path=\'$spath\') and \r\nrex_article.startpage=0 and rex_article.status = 1 \r\norder by rex_article.prior,rex_article.name\");\r\n\r\nfor ($i=0;$i<$GC->getRows();$i++)\r\n{\r\n $KAL[$GC->getValue(\"rex_article.online_von\")] = $GC->getValue(\"rex_article.id\");\r\n $KALNAME[$GC->getValue(\"rex_article.online_von\")] = $GC->getValue(\"rex_article.name\");\r\n $GC->next();\r\n}\r\n\r\n\r\n// ----- KALENDER\r\n\r\nif ($FORM[date]!=\"\") \r\n{ \r\n $FORM[year] = substr($FORM[date],0,4); \r\n $FORM[month] = substr($FORM[date],4,2); \r\n $FORM[day] = substr($FORM[date],6,2); \r\n} \r\n\r\nif (!checkdate($FORM[month],$FORM[day],$FORM[year])) \r\n\r\n{ \r\n $FORM[year] = date(\"Y\"); \r\n $FORM[month] = date(\"m\"); \r\n $FORM[day] = date(\"d\"); \r\n} \r\n\r\n$FORM[date] = $FORM[year].$FORM[month].$FORM[day]; \r\n\r\n$today = date(\"Ymd\");\r\n$FORM[link] = \"index.php?article_id=REX_ARTICLE_ID\";\r\n$link = $FORM[link];\r\n$month_days = date(\"j\",mktime(0,0,0,$FORM[month]+1,1,$FORM[year])-1);\r\n$month_before = $FORM[month]-1;\r\n$month_later = $FORM[month]+1;\r\n$year_before = $FORM[year];\r\n$year_later = $FORM[year];\r\nif ($month_before<1){ $month_before = 12; $year_before= $year_before-1;}\r\nif ($month_later>12){ $month_later = 1; $year_later= $year_later+1;}\r\nif ($month_before<10){ $month_before = \"0$month_before\"; }\r\nif ($month_later<10){ $month_later = \"0$month_later\"; }\r\n$before = $year_before.$month_before.\"01\";\r\n$later  = $year_later.$month_later.\"01\";\r\n\r\n$KALENDER .= \"\r\n	<div class=Calendar>\";\r\n$KALENDER .= \"\r\n	<div class=CalendarHead>\r\n		<div class=CalendarHeadLeft><a href=$link&FORM[date]=$before class=calhead><b>&laquo;</b></a>&nbsp;<a href=$link&FORM[date]=$later class=calhead><b>&raquo;</b></a></div>\r\n		<div class=CalendarHeadRight>\".$FORM[month].\"/\".$FORM[year].\"</div>\r\n	</div>\";\r\n$KALENDER .= \"\r\n	<div class=CalendarWeekdays>\r\n		<span>Mo</span>\r\n		<span>Tu</span>\r\n		<span>We</span>\r\n		<span>Th</span>\r\n		<span>Fr</span>\r\n		<span>Sa</span>\r\n		<span>Su</span>\r\n	</div>\";\r\n	\r\n$k = 1;\r\nfor ($i=1;$i<7;$i++)\r\n{\r\n $KALENDER .= \"\r\n	<div class=CalendarDays>\";\r\n	\r\n for($j=0;$j<7;$j++)\r\n {\r\n  $k = $k + 0;\r\n  if ($k<10) $k=\"0$k\";\r\n  if ($FORM[month]<10) $comp_month=\"\".$FORM[month]; \r\n  else $comp_month=$FORM[month];\r\n  $which_weekday = date(\"w\",mktime(0,0,0,$FORM[month],$k-1,$FORM[year]));\r\n  if ($which_weekday == $j and $month_days>=$k)\r\n  { 	\r\n   $loop_day = $FORM[year].\"$comp_month$k\";\r\n   $KALENDER .= \"\r\n		<span\";\r\n   if ($FORM[date] == $loop_day) $KALENDER .= \" class=CalendarDaysActive>\";\r\n   elseif ($today == $loop_day) $KALENDER .= \" class=CalendarDaysIdontknow>\";\r\n   elseif ($j == 5 or $j == 6) $KALENDER .= \" class=CalendarDaysWeekenddays>\";\r\n   else $KALENDER .= \" class=CalendarDaysWeekdays>\";\r\n   if ($KAL[$loop_day] != \"\") $KALENDER .= \"<a href=index.php?article_id=\".$KAL[$loop_day].\"&FORM[date]=$loop_day>$k</a>\";\r\n   else $KALENDER .= \"$k\";\r\n   $KALENDER .= \"\r\n		</span>\";\r\n   $k++;\r\n  }else\r\n  {\r\n   $KALENDER .= \"<span></span>\";\r\n  }\r\n }\r\n $KALENDER .= \"</div>\";\r\n if ($month_days<$k) break;\r\n}\r\n$KALENDER .= \"</div>\";\r\n\r\n// ----- / KALENDER\r\n\r\necho \"<div class=CalendarContainer>$KALENDER<div class=Clear>&nbsp;</div></div>\";\r\n\r\n?>','','<br><br>','','0','0','','','','0','0');
INSERT INTO rex_modultyp VALUES ('10','','04.01 - Bild','0','<?\r\n\r\nif (\"FILE[1]\" != \"\")\r\n{\r\n echo \"<img src=$REX[HTDOCS_PATH]/files/FILE[1] width=100>\";\r\n}\r\n\r\n?><br><br>','','REX_MEDIA_BUTTON[1]\r\n\r\n<?\r\n\r\nif (\"FILE[1]\" != \"\")\r\n{\r\n echo \"<img src=$REX[HTDOCS_PATH]/files/FILE[1]>\";\r\n}\r\n\r\n?><br><br>','','0','0','','','','0','0');
INSERT INTO rex_template VALUES ('1','','01.01 Default','<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/2000/REC-xhtml1-20000126/DTD/xhtml1-transitional.dtd\">\r\n<html>\r\n<?\r\n\r\nsetlocale(LC_ALL,\"de_DE\");\r\n\r\n?>\r\n<head>\r\n<title>Redaxo Demo - CSS</title>\r\n<link rel=stylesheet type=text/css href=<? echo $REX[HTDOCS_PATH] ?>files/import.css>\r\n<script language=Javascript src=<? echo $REX[HTDOCS_PATH] ?>js/standard.js></script>\r\n<script language=Javascript src=<? echo $REX[HTDOCS_PATH] ?>js/flashdetect.js></script>\r\n\r\n\r\n</head>\r\n<body>\r\n<a name=top></a>\r\n<div class=\"Website\">\r\n\r\n\r\n	<div class=\"LeftContainer\">\r\n		<div class=\"Logo\"><img src=<? echo $REX[HTDOCS_PATH] ?>files/logo.gif></div>\r\n		<div class=\"MenuContainer\"><? include $REX[INCLUDE_PATH].\"/generated/templates/2.template\"; ?></div>\r\n	</div>		\r\n\r\n\r\n	<div class=\"RightContainer\">\r\n		<div class=\"Banner\"><? include $REX[INCLUDE_PATH].\"/generated/templates/5.template\"; ?></div>\r\n		<div class=\"ContentContainer\"><? echo $this->getValue(\"name\"); echo $this->getArticle(); ?></div>\r\n\r\n	<div class=\"Copyright\">created 2004 by <a href=http://www.pergopa.de target=_blank>pergopa kristinus gbr</a> |  <a href=<? echo rex_getUrl(12,$clang);?>>Impressum</a> | wcms: <a href=http://www.redaxo.de target=_blank>redaxo 2.7</a></div>\r\n	</div>\r\n\r\n	<br style=\"clear:both;\" />\r\n</div>\r\n\r\n</body>\r\n</html>','','1','20050531190436','','','0','0');
INSERT INTO rex_template VALUES ('2','','02.01 Navigation','<?php\r\n\r\n// Pfad auslesen und als Array speichern\r\n$PATH = explode(\"|\",$this->getValue(\"path\").$this->getValue(\"article_id\").\"|\");\r\n$path1 = $PATH[1];\r\n$path2 = $PATH[2];\r\n$path3 = $PATH[3];\r\n$path4 = $PATH[4];\r\n\r\necho \"<br> 1:$path1 2:$path2 3:$path3 4:$path4\";\r\n\r\n\r\n// erst ebene ul starten\r\necho \'<ul class=\"nav1st\">\';\r\n\r\n/* START 1st level categories */\r\nforeach (OOCategory::getRootCategories() as $lev1):\r\n	\r\n	$lev1->setClang($REX[CUR_CLANG]);\r\n	\r\n	if ($lev1->isOnline()):\r\n	\r\n		if ($lev1->getId() == $path1)\r\n		{\r\n			/* 1st level - active link */\r\n			echo \'<li class=\"active\">X <a href=\"\'.$lev1->getUrl().\'\">\'.$lev1->getName().\'</a>\';\r\n		}else\r\n		{\r\n			/* 1st level - no active link */\r\n			echo \'<li><a href=\"\'.$lev1->getUrl().\'\">\'.$lev1->getName().\'</a>\';\r\n		}\r\n		\r\n		/* 1st level had categories? -> go on */\r\n		$lev1Size = sizeof($lev1->getChildren());\r\n		\r\n		if($lev1Size != \"0\" && $lev1->getId() == $path1): \r\n\r\n			echo \'<ul class=\"nav2nd\">\';\r\n\r\n			/* START 2nd level categories */\r\n			foreach ($lev1->getChildren() as $lev2):\r\n\r\n				if ($lev2->isOnline()):\r\n	\r\n					if ($lev2->getId() == $path2)\r\n					{\r\n						/* 2nd level - active link */\r\n						echo \'<li class=\"active\">X <a class=\"current\" href=\"\'.$lev2->getUrl().\'\">\'.$lev2->getName().\'</a>\';\r\n					}else\r\n					{\r\n						/* 2nd level - no active link */\r\n						echo \'<li><a href=\"\'.$lev2->getUrl().\'\">\'.$lev2->getName().\'</a>\';\r\n					}\r\n					\r\n					/* 2nd level had categories? -> go on */\r\n					$lev2Size = sizeof($lev2->getChildren());\r\n			\r\n					if($lev2Size != \"0\" && $lev2->getId() == $path2):\r\n						echo \'<ul class=\"nav3rd\">\';\r\n					\r\n						/* START 3rd level categories */\r\n						foreach ($lev2->getChildren() as $lev3):\r\n							if ($lev3->isOnline()):\r\n								if ($lev3->getId() == $path3)\r\n								{\r\n									/* 3rd level - active link */\r\n									echo \'<li class=\"active\">X <a class=\"current\" href=\"\'.$lev3->getUrl().\'\">\'.$lev3->getName().\'</a></li>\';\r\n								}else\r\n								{\r\n									/* 3rd level - no active link */\r\n									echo \'<li><a href=\"\'.$lev3->getUrl().\'\">\'.$lev3->getName().\'</a></li>\';\r\n								}\r\n							endif;\r\n						endforeach;\r\n						/* END 3rd level categories */\r\n			\r\n						echo \'</ul>\';\r\n					endif;\r\n\r\n					echo \'</li>\';\r\n				endif;\r\n			\r\n			endforeach;\r\n			/* END 2nd level categories */\r\n			\r\n			echo \'</ul>\';\r\n		endif;\r\n\r\n		echo \'</li>\';\r\n	endif;\r\nendforeach;\r\n/* END 1st level categories */\r\n\r\necho \'</ul>\';\r\n?>','','0','20050531192320','','','0','0');
INSERT INTO rex_template VALUES ('5','','03.01 Banner','<a href=http://www.redaxo.de><img src=<? echo $REX[HTDOCS_PATH]; ?>files/banner.jpg width=468 height=60 border=0></a>','','0','20041028000000','','','0','0');
