INSERT INTO rex_modultyp VALUES ('6','','04 - Download','0','<?php\r\n\r\n$jahr_von = \'REX_VALUE[5]\';\r\n$monat_von = \'REX_VALUE[6]\';\r\n$tag_von = \'REX_VALUE[7]\';\r\n\r\n$jahr_bis = \'REX_VALUE[8]\';\r\n$monat_bis = \'REX_VALUE[9]\';\r\n$tag_bis = \'REX_VALUE[10]\';\r\n\r\nif ( $jahr_von != \'\'\r\n	AND $monat_von != \'\'\r\n	AND $tag_von != \'\'\r\n	AND $jahr_bis != \'\'\r\n	AND $monat_bis != \'\'\r\n	AND $tag_bis != \'\') {\r\n	\r\n	$von = mktime( 0, 0, 0, $monat_von, $tag_von, $jahr_von);\r\n	$bis = mktime( 23, 59, 0, $monat_bis, $tag_bis, $jahr_bis);\r\n	$jetzt = time();\r\n	\r\n	if( $jetzt >= $von && $jetzt <= $bis) {\r\n		if ($REX[\'REDAXO\'] != true) {\r\n	\r\n			if (!function_exists(\'Dateigroesse\')) {\r\n				function Dateigroesse($URL) {\r\n					$Groesse = filesize($URL);\r\n					if($Groesse<1000) {\r\n						return number_format($Groesse, 0, \",\", \".\").\" Bytes\";\r\n					}\r\n					elseif($Groesse<1000000) {\r\n						return number_format($Groesse/1024, 0, \",\", \".\").\" kB\";\r\n					}\r\n					else {\r\n						return number_format($Groesse/1048576, 0, \",\", \".\").\" MB\";\r\n					}\r\n				}\r\n			}\r\n	\r\n			if (!function_exists(\'parse_icon\')) {\r\n				function parse_icon($ext) {\r\n					switch (strtolower($ext)) {\r\n	\r\n					case \'doc\': return \'icon_doc.gif\';\r\n					case \'rtf\': return \'icon_doc.gif\';\r\n					case \'txt\': return \'icon_txt.gif\';\r\n					case \'xls\': return \'icon_xls.gif\';\r\n					case \'csv\': return \'icon_xls.gif\';\r\n					case \'ppt\': return \'icon_ppt.gif\';\r\n					case \'html\': return \'icon_html.gif\';\r\n					case \'htm\': return \'icon_html.gif\';\r\n					case \'php\': return \'icon_script.gif\';\r\n					case \'php3\': return \'icon_script.gif\';\r\n					case \'cgi\': return \'icon_script.gif\';\r\n					case \'pdf\': return \'icon_pdf.gif\';\r\n					case \'rar\': return \'icon_rar.gif\';\r\n					case \'zip\': return \'icon_zip.gif\';\r\n					case \'gz\': return \'icon_gz.gif\';\r\n					case \'jpg\': return \'icon_jpg.gif\';\r\n					case \'gif\': return \'icon_gif.gif\';\r\n					case \'png\': return \'icon_png.gif\';\r\n					case \'bmp\': return \'icon_image.gif\';\r\n					case \'tif\': return \'icon_image.gif\';\r\n					case \'exe\': return \'icon_binary.gif\';\r\n					case \'bin\': return \'icon_binary.gif\';\r\n					case \'avi\': return \'icon_mov.gif\';\r\n					case \'mpg\': return \'icon_mov.gif\';\r\n					case \'moc\': return \'icon_mov.gif\';\r\n					case \'asf\': return \'icon_mov.gif\';\r\n					case \'mp3\': return \'icon_sound.gif\';\r\n					case \'wav\': return \'icon_sound.gif\';\r\n					case \'org\': return \'icon_sound.gif\';\r\n				\r\n					default:\r\n					return \'icon_def.gif\';\r\n					}\r\n				}\r\n			}\r\n			$ext = substr(strrchr(\'REX_FILE[1]\', \'.\'), 1);\r\n			$parsed_icon = $REX[\'HTDOCS_PATH\'].\'/files/\'.parse_icon($ext);\r\n			$ooPic = OOMedia::getMediaByName (\'REX_FILE[1]\');\r\n			$fileDescription = $ooPic->getDescription();\r\n	\r\n			print \'\r\n				<div class=\"download-hdl\"><p>REX_VALUE[1]</p></div>\r\n				<div class=\"download-file\"><p><a href=\"\'.$REX[\'HTDOCS_PATH\'].\'files/REX_FILE[1]\" target=\"_blank\"><img src=\"\'.$parsed_icon.\'\" alt=\"Download\" /></a> REX_FILE[1] - \'.Dateigroesse($REX[\'HTDOCS_PATH\'].\"files/REX_FILE[1]\").\'</p></div>\';\r\n	\r\n			if ($fileDescription != \"\") { \r\n				print \'<div class=\"download-desc\"><p>\'.$fileDescription.\'</p></div>\';\r\n			}\r\n			else {\r\n				print \'<div class=\"download-without-desc\"> </div>\';\r\n			}\r\n		}\r\n		else {\r\n			print \"REX_VALUE[1]REX_VALUE[2]\";\r\n		}\r\n	} /* ende von if( $jetzt >= $von && $jetzt <= $bis)*/\r\n} \r\n?>','','Bezeichnung des Download: \r\n<br><input type=text size=40 name=VALUE[1] value=\"REX_VALUE[1]\"><br /><br />\r\nREX_MEDIA_BUTTON[1]<br>\r\n<strong>Hinweis: Die Beschreibung des Downloads erfolgt im Medienpool!</strong><br />\r\n<br />\r\n<?php\r\n$jahr = date(\'Y\');\r\n$monat = date(\'m\');\r\n$tag = date(\'d\');\r\n\r\n$jahr_von_val = \'REX_VALUE[5]\';\r\n$monat_von_val = \'REX_VALUE[6]\';\r\n$tag_von_val = \'REX_VALUE[7]\';\r\n\r\n$jahr_bis_val = \'REX_VALUE[8]\';\r\n$monat_bis_val = \'REX_VALUE[9]\';\r\n$tag_bis_val = \'REX_VALUE[10]\';\r\n\r\nif ( empty($jahr_von_val)) $jahr_von_val = $jahr;\r\nif ( empty($monat_von_val)) $monat_von_val = $monat;\r\nif ( empty($tag_von_val)) $tag_von_val = $tag;\r\n\r\nif ( empty($jahr_bis_val)) $jahr_bis_val = $jahr +9;\r\nif ( empty($monat_bis_val)) $monat_bis_val = $monat;\r\nif ( empty($tag_bis_val)) $tag_bis_val = $tag;\r\n\r\n?>\r\nOnline von:<select name=\"VALUE[7]\" >\r\n<?php\r\nforeach (range(1,31) as $tag_von) {\r\n   echo \'<option value=\"\'.$tag_von.\'\" \';\r\n   \r\n   if ( $tag_von_val==$tag_von ) {\r\n      echo \'selected=\"selected\" \';\r\n   }\r\n   echo \'>\'.$tag_von.\'</option>\';\r\n}\r\n?>\r\n</select><select name=\"VALUE[6]\" >\r\n<?php\r\nforeach (range(1,12) as $monat_von) {\r\n   echo \'<option value=\"\'.$monat_von.\'\" \';\r\n   \r\n   if ( $monat_von_val==$monat_von ) {\r\n      echo \'selected=\"selected\" \';\r\n   }\r\n   echo \'>\'.$monat_von.\'</option>\';\r\n}\r\n?>\r\n\r\n</select>\r\n<select name=\"VALUE[5]\" >\r\n<?php\r\nforeach (range( $jahr,$jahr+9) as $jahr_von) {\r\n   echo \'<option value=\"\'.$jahr_von.\'\" \';\r\n   \r\n   if ( $jahr_von_val ==$jahr_von ) {\r\n      echo \'selected=\"selected\" \';\r\n   }\r\n   echo \'>\'.$jahr_von.\'</option>\';\r\n}\r\n?>\r\n\r\n</select><br /><br />\r\n\r\nOnline bis:<select name=\"VALUE[10]\" >\r\n<?php\r\nforeach (range(1,31) as $tag_bis) {\r\n   echo \'<option value=\"\'.$tag_bis.\'\" \';\r\n   \r\n   if ( $tag_bis_val==$tag_bis ) {\r\n      echo \'selected=\"selected\" \';\r\n   }\r\n   echo \'>\'.$tag_bis.\'</option>\';\r\n}\r\n?>\r\n</select><select name=\"VALUE[9]\" >\r\n<?php\r\nforeach (range(1,12) as $monat_bis) {\r\n   echo \'<option value=\"\'.$monat_bis.\'\" \';\r\n   \r\n   if ( $monat_bis_val==$monat_bis ) {\r\n      echo \'selected=\"selected\" \';\r\n   }\r\n   echo \'>\'.$monat_bis.\'</option>\';\r\n}\r\n?>\r\n\r\n</select>\r\n<select name=\"VALUE[8]\" >\r\n<?php\r\nforeach (range($jahr,$jahr+9) as $jahr_bis) {\r\n   echo \'<option value=\"\'.$jahr_bis.\'\" \';\r\n   \r\n   if ( $jahr_bis_val==$jahr_bis ) {\r\n      echo \'selected=\"selected\" \';\r\n   }\r\n   echo \'>\'.$jahr_bis.\'</option>\';\r\n}\r\n?>\r\n\r\n</select><br/><br/>\r\n\r\n<?php\r\n\r\nif ( $jahr_von_val != \'\'\r\n     && $monat_von_val != \'\'\r\n     && $tag_von_val != \'\'\r\n     && $jahr_bis_val != \'\'\r\n     && $monat_bis_val != \'\'\r\n     && $tag_bis_val != \'\') {\r\n  $von = mktime( 0, 0, 0, $monat_von_val, $tag_von_val, $jahr_von_val);\r\n  $bis  = mktime( 23, 59, 0, $monat_bis_val, $tag_bis_val, $jahr_bis_val);\r\n  $jetzt = time();\r\n\r\n  if( $jetzt >= $von && $jetzt <= $bis) {\r\n    $state = \'<span style=\"color: #00aa00;\">online</span>\';\r\n  } else {\r\n    $state = \'<span style=\"color: #aa0000;\">offline</span>\';\r\n  }\r\n\r\n  echo \'Download aufgrund der Datumseinstellungen \'.$state;\r\n} \r\n?>','','0','0','','','','0','0');
INSERT INTO rex_modultyp VALUES ('7','','05 - Teaser','0','<?php\r\n\r\n$itemsPerSide = \"REX_VALUE[1]\";\r\n$wordsPerArticle = \"REX_VALUE[2]\";\r\n\r\n// Nur im Frontend\r\n//if (!$REX[\'REDAXO\']):\r\n\r\n// Sortierfunktion\r\nif (!function_exists(sortArticlesByUpdateDate)) {\r\n	function sortArticlesByUpdateDate( $artA, $artB) {\r\n		$createA = $artA->_updatedate;\r\n		$createB = $artB->_updatedate;\r\n\r\n		if ( $createA == $createB) {\r\n			return 0;\r\n		}\r\n\r\n		return $createA > $createB ? -1 : 1;\r\n	}\r\n}\r\n\r\n$start = !empty($_GET[\'start\']) ? (int) $_GET[\'start\'] : 0; // Startitem\r\n$offset = $itemsPerSide; // 10 Items pro Seite\r\n$cats = array( REX_CATEGORY_ID); // alle Kategorien die Du \"indizieren\" willst\r\n\r\n$articles = array();\r\n\r\nforeach ( $cats as $catId) {\r\n  $cat = OOCategory::getCategoryById( $catId);\r\n \r\n  // CatIds die nicht funktionieren aussortieren\r\n  if( $cat === null) {\r\n     continue;\r\n  }\r\n\r\n  $cat_articles = $cat->getArticles( true);\r\n\r\n  foreach ( $cat_articles as $ooarticle) {\r\n     // keine Startartikel anzeigen\r\n    if ( $ooarticle->isStartPage()) {\r\n       continue;\r\n     }\r\n\r\n     // Damit keine Endlosschleife passiert, den Artikel der die Pagination setzt überspringen\r\n     if ( $ooarticle->getId() == REX_ARTICLE_ID) {\r\n        continue;\r\n     }\r\n     $articles[] = $ooarticle;\r\n  }\r\n}\r\n\r\n$articles_length = count( $articles);\r\n\r\n// Sortieren nach Erstellungsdatum\r\nif ( $articles_length > 0) {\r\n\r\n   usort( $articles, \"sortArticlesByUpdateDate\");\r\n}\r\n\r\n$article_list = array_slice( $articles, $start, $offset);\r\n\r\nforeach ( $article_list as $ooarticle) {\r\n	\r\n	if ($ooarticle->isOnline(true)) {\r\n		$articleId = $ooarticle->getId();\r\n\r\n		$article = new article();\r\n		$article->setArticleId( $articleId);\r\n		$articleContent = $article->getArticle();\r\n	\r\n		// Leerzeichen entfernen am Anfang und Ende des Strings\r\n		$articleContent = trim($articleContent);\r\n		\r\n		// Leerzeichen vor </p> einfuegen.\r\n		// Sonst wird das letzte Wort eines Absatzes und das erste Wort \r\n		// des nachfolgenden Absatzes als ein Wort erkannt\r\n		$articleContent = str_replace(\"</p>\", \" </p>\", $articleContent);\r\n		\r\n		// Leerzeichen vor <br /> einfuegen.\r\n		// Sonst wird das letzte Wort einer Zeile und das erste Wort \r\n		// des nachfolgenden Zeile als ein Wort erkannt\r\n		$articleContent = str_replace(\"<br />\", \" <br />\", $articleContent);\r\n		\r\n		$output = \"\";\r\n		$words = explode(\" \",$articleContent);\r\n		$wordsCount = count($words);\r\n	\r\n		if ($wordsCount < $wordsPerArticle) $wEnd = $wordsCount;\r\n		else  $wEnd = $wordsPerArticle;\r\n				\r\n		for ($w=0;$w<$wEnd;$w++) {\r\n			$output .= $words[$w].\" \";\r\n		}\r\n		\r\n		// Leerzeichen entfernen am Anfang und Ende des Strings	\r\n		$output = trim($output);\r\n	\r\n		$isCloseParagraph = substr($output,-4);\r\n		$link = \'<a href=\"\'.rex_getUrl($articleId).\'\">...mehr</a>\';\r\n		$newString = $link.\'</p>\';\r\n	\r\n	\r\n		if ($isCloseParagraph == \'</p>\') {\r\n			$output = substr_replace($output,$newString,-4);\r\n		}\r\n		else {\r\n			$output .= $newString;\r\n		}\r\n	\r\n		print \'<div class=\"teaser\">\'.$output.\'</div>\';\r\n	}\r\n}\r\n\r\n$prevStart = $start - $offset;\r\nif ( $prevStart < 0)\r\n{\r\n   $prevStart = \'\';\r\n}\r\n\r\n$nextStart = $start + $offset;\r\nif ( $nextStart >= $articles_length)\r\n{\r\n   $nextStart = \'\';\r\n}\r\n\r\n$link_format = \'<a href=\"index.php?article_id=REX_ARTICLE_ID&start=%s\">%s</a>\';\r\n\r\n// Vorherige Seite Link\r\nif ( $prevStart !== \'\') {\r\n  $backLink = sprintf( $link_format, $prevStart, \'<< vorherige Seite\');\r\n}\r\n\r\n// Nächste Seite\r\nif ( $nextStart !== \'\') {\r\n  $nextLink = sprintf( $link_format, $nextStart, \'n&auml;chste Seite >>\');\r\n}\r\n\r\nif ($backLink != \"\" AND $nextLink != \"\") {\r\n	print \'<div class=\"teaserNav\"><p>\'.$backLink.\'&nbsp;|&nbsp;\'.$nextLink.\'</p></div>\';\r\n}\r\nelseif ($nextLink != \"\") {\r\n	print \'<div class=\"teaserNav\"><p>\'.$nextLink.\'</p></div>\';\r\n}\r\nelseif ($backLink != \"\") {\r\n	print \'<div class=\"teaserNav\"><p>\'.$backLink.\'</p></div>\';\r\n}\r\n\r\n//endif;\r\n\r\n?>\r\n','','<strong>Anzahl der Artikel pro Seite</strong><br />\r\n<input name=\"VALUE[1]\" value=\"REX_VALUE[1]\" class=\"inp100\" /><br />\r\n<br />\r\n<strong>Anzahl der Wörter pro Artikel</strong><br />\r\n<input name=\"VALUE[2]\" value=\"REX_VALUE[2]\" class=\"inp100\" /><br />','','0','0','','','','0','0');
INSERT INTO rex_modultyp VALUES ('13','','06 - Formular erzeugen','0','<?php\r\n$rex_form_data = <<<End\r\nREX_HTML_VALUE[3]\r\nEnd;\r\n\r\n$mailbody = <<<End\r\nREX_HTML_VALUE[5]\r\nEnd;\r\n\r\n$formname = \"kontaktform\";\r\n$submitlabel = \"REX_VALUE[7]\";\r\n$style = \' style=\"color:#f00;\"\';\r\n\r\n\r\n$formoutput = array();\r\n$warning = array();\r\n$form_elements = array();\r\n$form_elements = explode(\"\\n\", $rex_form_data);\r\n\r\nfor($i=0;$i<count($form_elements);$i++){\r\n\r\n	$element = explode(\"|\", $form_elements[$i]);\r\n	$AFE[$i] = $element;\r\n\r\n	switch($element[0]){\r\n\r\n		case \"headline\":\r\n			$formoutput[] = \'<p>\'.$element[1].\'</p>\';\r\n		break;\r\n\r\n		case \"text\":\r\n			if($FORM[$formname][\"el_\".$i] == \"\" && !$FORM[$formname][$formname.\"send\"]){ \r\n				$FORM[$formname][\"el_\".$i] = trim($element[3]); \r\n			}\r\n		\r\n			if($element[2] == 1 && (trim($FORM[$formname][\"el_\".$i]) == \"\" || trim($FORM[$formname][\"el_\".$i]) == trim($element[3])) && $FORM[$formname][$formname.\"send\"] == 1){ \r\n				$warning[\"el_\".$i] = $style; \r\n			}\r\n			\r\n			$formoutput[] = \'\r\n				<label class=\"text\" \'.$warning[\"el_\".$i].\' for=\"el_\'.$i.\'\" >\'.$element[1].\'</label>\r\n				<input type=\"text\" class=\"text\" name=\"FORM[\'.$formname.\'][el_\'.$i.\']\" id=\"el_\'.$i.\'\" value=\"\'.htmlentities(stripslashes($FORM[$formname][\"el_\".$i])).\'\" /><br />\r\n				\';\r\n		break;\r\n		\r\n		case \"textarea\":\r\n			if($FORM[$formname][\"el_\".$i] == \"\" && !$FORM[$formname][$formname.\"send\"]){ \r\n				$FORM[$formname][\"el_\".$i] = $element[3]; \r\n			}\r\n		\r\n			if($element[2] == 1 && (trim($FORM[$formname][\"el_\".$i]) == \"\" || trim($FORM[$formname][\"el_\".$i]) == trim($element[3])) && $FORM[$formname][$formname.\"send\"] == 1){ \r\n				$warning[\"el_\".$i] = $style; \r\n			}\r\n		\r\n			$formoutput[] = \'\r\n				<label class=\"textarea\" \'.$warning[\"el_\".$i].\' for=\"el_\'.$i.\'\" >\'.$element[1].\'</label>\r\n				<textarea class=\"textarea\" name=\"FORM[\'.$formname.\'][el_\'.$i.\']\" id=\"el_\'.$i.\'\" cols=\"80\" rows=\"10\">\'.htmlentities(stripslashes($FORM[$formname][\"el_\".$i])).\'</textarea><br />\r\n				\';\r\n		break;\r\n	\r\n	\r\n		case \"select\":\r\n\r\n			$SEL = new select();\r\n			$SEL->set_name(\"FORM[\".$formname.\"][el_\".$i.\"]\");\r\n			$SEL->set_id(\"el_\".$i);\r\n			$SEL->set_size(1);\r\n			$SEL->set_style(\' class=\"select\"\');\r\n		\r\n			if($FORM[$formname][\"el_\".$i] == \"\" && !$FORM[$formname][$formname.\"send\"]){ \r\n				$SEL->set_selected($element[3]); } else { $SEL->set_selected($FORM[$formname][\"el_\".$i]); \r\n			}\r\n\r\n			foreach(explode(\";\", trim($element[4])) as $v){\r\n				$SEL->add_option( $v, $v);\r\n			}\r\n		\r\n			if($element[2] == 1 && trim($FORM[$formname][\"el_\".$i] )== \"\" && $FORM[$formname][$formname.\"send\"] == 1){ \r\n				$warning[\"el_\".$i] = $style; \r\n			}\r\n		\r\n			$formoutput[] = \'\r\n				<label class=\"select\" \'.$warning[\"el_\".$i].\' for=\"el_\'.$i.\'\" >\'.$element[1].\'</label>\r\n				\'.$SEL->out().\'<br />\';\r\n		break;\r\n			\r\n		case \"checkbox\":\r\n		\r\n			if(	(trim($FORM[$formname][\"el_\".$i] )== \"1\" ) || ($FORM[$formname][\"el_\".$i] == \"\" && !$FORM[$formname][$formname.\"send\"] && $element[3] == 1)){ \r\n				$checked = \' checked=\"checked\"\'; \r\n				$hidden=\"\";\r\n			} \r\n			else { \r\n				$checked = \"\"; \r\n				$hidden = \'<input type=\"hidden\" name=\"FORM[\'.$formname.\'][el_\'.$i.\']\" value=\"0\" /><br />\'; \r\n			}\r\n\r\n			if($element[2] == 1 && trim($FORM[$formname][\"el_\".$i]) == \"\" && $FORM[$formname][$formname.\"send\"] == 1){ \r\n				$warning[\"el_\".$i] = $style; \r\n			}\r\n		\r\n			$formoutput[] = \r\n					$hidden.\'\r\n					<input type=\"checkbox\" class=\"checkbox\" name=\"FORM[\'.$formname.\'][el_\'.$i.\']\" id=\"el_\'.$i.\'\" value=\"1\" \'.$checked.\' />\r\n					<label class=\"checkbox\" \'.$warning[\"el_\".$i].\' for=\"el_\'.$i.\'\" >\'.$element[1].\'</label><br />\r\n					\';\r\n		break;\r\n	}\r\n}\r\n\r\n$out = \'\r\n	<div id=\"form-module\">\r\n	<form action=\"\'.rex_getUrl($this->getValue(\"article_id\")).\'\" method=\"post\">\r\n		<fieldset>\r\n\r\n		<legend>\'.$formname.\'</legend>\r\n		<input type=\"hidden\" name=\"FORM[\'.$formname.\'][\'.$formname.\'send]\" value=\"1\" />\';\r\n\r\nforeach($formoutput as $v){\r\n	$out .= \'\r\n		<div class=\"element\">\'.$v.\'</div>\';\r\n}\r\n$out .= \'\r\n		<div class=\"element\">\r\n			<input type=\"submit\" name=\"FORM[\'.$formname.\'][\'.$formname.\'submit]\" value=\"\'.$submitlabel.\'\" class=\"submit\" />\r\n		</div>\r\n		</fieldset>\r\n	</form>\r\n	</div>\';\r\n\r\nif($FORM[$formname][$formname.\"send\"] == 1 && count($warning)==0){\r\n	$mail = new PHPMailer();\r\n	$mail->AddAddress(\"REX_VALUE[1]\");\r\n	$mail->From = \"REX_VALUE[2]\";\r\n	$mail->Subject = \"REX_VALUE[4]\";\r\n	\r\n	foreach($FORM[$formname] as $k=>$v){\r\n		if($k != $formname.\"submit\" && $k != $formname.\"send\")\r\n		$mailbody .= $AFE[ereg_replace(\"el_\",\"\",$k)][1].\" \".stripslashes($v).\"\\n\";\r\n	}\r\n	\r\n	$mail->Body = $mailbody;\r\n	$mail->Send();\r\n	\r\n	print \'<div id=\"form-module-thanks\">\'.\"REX_VALUE[6]\".\'</div>\';\r\n\r\n} \r\nelse {\r\n	print $out;\r\n}\r\n?>','','Email geht an:<br />\r\n<input type=\"text\" name=\"VALUE[1]\" value=\"REX_VALUE[1]\" class=\"inp100\" /><br />\r\n<br />\r\nAbsender:<br />\r\n<input type=\"text\" name=\"VALUE[2]\" value=\"REX_VALUE[2]\" class=\"inp100\" /><br />\r\n<br />\r\nSubject:<br />\r\n<input type=\"text\" name=\"VALUE[4]\" value=\"REX_VALUE[4]\" class=\"inp100\" /><br />\r\n<br />\r\nMailbody Prefix:<br />\r\n<textarea name=\"VALUE[5]\" style=\"width:100%;height:100px;\">REX_VALUE[5]</textarea><br />\r\n<br />\r\nSubmitbutton Bezeichnung:<br />\r\n<input type=\"text\" name=\"VALUE[7]\" value=\"REX_VALUE[7]\" class=\"inp100\" /><br />\r\n<br />\r\nDanksagung:<br />\r\n<textarea name=\"VALUE[6]\" style=\"width:100%;height