INSERT INTO rex_template VALUES ('3','','navigation - horizontal - vertical','<?php\r\n// EXPLODE PATH\r\n$PATH = explode(\"|\",$this->getValue(\"path\").$this->getValue(\"article_id\").\"|\");\r\n\r\n// GET CURRENTS\r\n$path1 = $PATH[1];\r\n$path2 = $PATH[2];\r\n$path3 = $PATH[3];\r\n\r\n$user_typ = 1;\r\nif ($FORM[\'USR_TYP\'] != 0) {\r\n	$user_typ = $FORM[\'USR_TYP\'];\r\n}\r\n\r\n/* START 1st level categories */\r\n$navHeader .= \'<ul class=\"nav1st\">\';\r\nforeach (OOCategory::getRootCategories() as $lev1) {\r\n	\r\n	if ($user_typ >= $lev1->getTypeId()) {\r\n	if ($lev1->isOnline()):\r\n		if ($lev1->getId() == $path1) {\r\n			$navHeader .= \'<li class=\"active\"><a class=\"current\" href=\"\'.$lev1->getUrl().\'\">\'.$lev1->getName().\'</a>\';\r\n			if ($lev1->getId() != \"1\") {\r\n				$sitePath .= \' &#8250;&#8250; <a href=\"\'.$lev1->getUrl().\'\">\'.$lev1->getName().\'</a>\';\r\n			}\r\n			$titlePath .= \' >> \'.$lev1->getName();\r\n		}\r\n		// 2nd level - no active link\r\n		else {\r\n			$navHeader .= \'<li><a href=\"\'.$lev1->getUrl().\'\">\'.$lev1->getName().\'</a>\';\r\n		}\r\n			\r\n		// 1st level had categories? -> go on \r\n		$lev1Size = sizeof($lev1->getChildren());\r\n	\r\n		if ($lev1->getId() == $path1) {\r\n		if ($lev1Size != \"0\"):\r\n			$navLeftCol .= \'<ul class=\"nav2nd\">\';\r\n			$navLeftCol .= \'<li><strong>Unterkategorien</strong></li>\';\r\n\r\n\r\n			// START 2nd level categories \r\n			foreach ($lev1->getChildren() as $lev2):\r\n						\r\n				if ($lev2->isOnline()):\r\n	\r\n				// 2nd level - active link\r\n					if ($lev2->getId() == $path2) {\r\n						$navLeftCol .= \'<li class=\"active\"><a class=\"current\" href=\"\'.$lev2->getUrl().\'\">\'.$lev2->getName().\'</a></li>\';\r\n						$sitePath .= \' &#8250;&#8250; <a href=\"\'.$lev2->getUrl().\'\">\'.$lev2->getName().\'</a>\';\r\n						$titlePath .= \' >> \'.$lev2->getName();\r\n					}\r\n					// 2nd level - no active link\r\n					else {\r\n						$navLeftCol .= \'<li><a href=\"\'.$lev2->getUrl().\'\">\'.$lev2->getName().\'</a></li>\';\r\n					}\r\n			\r\n				endif;			\r\n				\r\n			endforeach;\r\n			// END 2nd level categories \r\n		\r\n			$navLeftCol .= \'</ul>\';\r\n		endif; // END by if ($lev1Size != \"0\"):\r\n		}\r\n		$navHeader .= \'</li>\';\r\n\r\n	endif; // END by if ($lev1->isOnline()) \r\n	}\r\n}\r\n$navHeader .= \'</ul>\';\r\n// END 1st level categories \r\n\r\nif ($REX[\'CUR_CLANG\'] == 1) $breadcrumb = \'You are here: <a href=\"/index.php?clang=1\">Startpage</a> \'.$sitePath;\r\nelse $breadcrumb = \'Sie befinden sich hier: <a href=\"/index.php\">Startseite</a> \'.$sitePath;\r\n\r\n\r\n\r\n?>','','0','20060120113246','','','0','0');
INSERT INTO rex_template VALUES ('4','','authentifizierung','<?\r\n$errorArticleId = \"20\";\r\n\r\nsetlocale(LC_ALL,\"de_DE\");\r\n\r\n// -------------------------------------------------------------- USER AUTH\r\n\r\nsession_start();\r\n\r\n$FORM[\'USR\'] = false;\r\n\r\n$user_id = 0;\r\n$user_name = \"\";\r\n\r\nif($REX[\'CUR_CLANG\']== \"0\"){\r\n	$error_lang = \"de\"; \r\n}\r\nelse{\r\n	$error_lang = \"en\"; \r\n}\r\n\r\nif ($_SESSION[\'UID\'][\'rexsite\'] !=\"\" OR $FORM[\'loginname\'] != \"\" OR $FORM[\'logout\'] != \"\") {\r\n\r\n	$FORM[\'USER\'] = new login();\r\n	$FORM[\'USER\']->setSqlDb(1);\r\n	$FORM[\'USER\']->setSysID(\"rexsite\");\r\n	$FORM[\'USER\']->setLanguage($error_lang);\r\n	$FORM[\'USER\']->setSessiontime(3000);\r\n	$FORM[\'USER\']->setLogin($FORM[\'loginname\'],$FORM[\'loginpasswort\']);\r\n	if ($FORM[\'LOGOUT\'] == 1) {\r\n		$FORM[\'USER\']->setLogout(true);\r\n	}\r\n	$FORM[\'USER\']->setUserID(\"rex_2_user.id\");\r\n	$FORM[\'USER\']->setUserquery(\"SELECT * \r\n								FROM 	rex_2_user \r\n								WHERE 	id=\'USR_UID\' \r\n								AND 	login_activation=1 \r\n								AND 	user_status=\'1\' \r\n								AND user_typ>\'0\'\");\r\n	$FORM[\'USER\']->setLoginquery(\"SELECT * \r\n								FROM 	rex_2_user \r\n								WHERE	user_login=\'USR_LOGIN\' \r\n								AND		user_password=\'USR_PSW\' \r\n								AND 	login_activation=\'1\' \r\n								AND		user_status=\'1\'\");\r\n\r\n	if ($FORM[\'USER\']->checkLogin()) {\r\n 		$FORM[\'USR\'] 	 = true;\r\n		$user_id 		 = $FORM[\'USER\']->getValue(\"rex_2_user.id\");\r\n		$user_name 		 = $FORM[\'USER\']->getValue(\"rex_2_user.user_name\");\r\n		$FORM[\'USR_ID\']  = $user_id;\r\n		$FORM[\'USR_TYP\'] = $FORM[\'USER\']->getValue(\"rex_2_user.user_typ\");\r\n	}\r\n	else {\r\n		$FORM[\'USR\'] = false;\r\n		$err_msg 		 = $FORM[\'USER\']->message;\r\n		$FORM[\'USR_ID\']  = 0;\r\n		$FORM[\'USR_TYP\'] = 0;\r\n	}\r\n}\r\nelse {\r\n	// nicht eingeloggt und kein login\r\n	$FORM[\'USR_ID\'] = 0;\r\n	$FORM[\'USR_TYP\'] = 0;\r\n}\r\n\r\n\r\n\r\n// EXPLODE PATH\r\n$PATH = explode(\"|\",$this->getValue(\"path\").$this->getValue(\"article_id\").\"|\");\r\n$aid = $this->getValue(\"article_id\");\r\n\r\n// GET CURRENTS\r\n$path1 = $PATH[1];\r\n$path2 = $PATH[2];\r\n$path3 = $PATH[3];\r\n\r\n$lev1 = OOCategory::getCategoryById($path1);\r\n\r\nif ($lev1->getTypeId() > 1 && !$FORM[USR]) {\r\n	$hack = true;\r\n}\r\nelse {\r\n	if ($lev1->getId() == $aid) {\r\n		$hack = false;\r\n	}\r\n	else {\r\n		if (sizeof($lev1->getChildren()) != 0) {\r\n			$lev2 = OOCategory::getCategoryById($path2);\r\n\r\n			if ($lev2->getTypeId() >1 && !$FORM[USR]) {\r\n				$hack = true;\r\n			}\r\n			else {\r\n				if ($lev1->getId() == $aid) {\r\n					$hack = false;\r\n				}\r\n			}\r\n		}\r\n	}\r\n}\r\n\r\nif ($hack) {\r\n	header(\"Location: http://\".$_SERVER[\'HTTP_HOST\'].\"/index.php?article_id=\".$errorArticleId.\"&clang=\".$REX[\'CUR_CLANG\']);\r\n	exit;\r\n}','','0','20060209204504','','','0','0');
INSERT INTO rex_template VALUES ('5','','login','<div class=\"login\"><div class=\"login2\">\r\n<?php\r\n\r\n$msg = \"\";\r\nif($FORM[\'USER\']->message != \"\") {\r\n	$msg = \'<p class=\"warning\">\'.$FORM[\'USER\']->message.\"</p>\";\r\n}\r\n\r\nif(!$FORM[\'USR\'] OR $FORM[\'LOGOUT\'] == 1) {\r\n	\r\n	$FORM[loginname] = \"Login\";\r\n	\r\n	if($REX[\'CUR_CLANG\']==\"0\") {	\r\n		$pwdLang = \"Passwort\";\r\n		$pwfLang = \"Passwort?\";\r\n	}\r\n	else {\r\n		$pwdLang = \"Password\";\r\n		$pwfLang = \"Password?\";\r\n	}\r\n\r\n	print \'\r\n	<form action=\"index.php\" method=\"post\">\r\n	<fieldset>\r\n	<input type=\"hidden\" name=\"article_id\" value=\"\'.$this->getValue(\"article_id\").\'\" />\r\n	<input type=\"hidden\" name=\"FORM[trytologin]\" value=\"1\" />\r\n	<input type=\"hidden\" name=\"clang\" value=\"\'.$REX[\'CUR_CLANG\'].\'\" />\r\n	\r\n	\'.$msg.\'\r\n	<label for=\"login\">Login</label>\r\n	<input id=\"login\" type=\"text\" name=\"FORM[loginname]\" value=\"\" />\r\n		\r\n	<label for=\"paswd\">\'.$pwdLang.\'</label>\r\n	<input id=\"paswd\" type=\"password\" name=\"FORM[loginpasswort]\" value=\"\" />\r\n	\r\n	<input class=\"submit\" type=\"submit\" value=\"anmelden\" />\r\n	\r\n	</fieldset>\r\n	</form>\r\n	\';\r\n}\r\nelse{\r\n	print \r\n	$msg.\'\r\n	<p style=\"text-align:left;\">Login: <strong>\'.$FORM[\'USER\']->getValue(\"user_login\").\'</strong></p>\r\n	<p style=\"text-align:left;\"><a href=\"index.php?article_id=\'.$this->getValue(\"article_id\").\'&clang=\'.$REX[\'CUR_CLANG\'].\'&FORM[LOGOUT]=1\">&laquo; Logout</a></p>\r\n	\';\r\n}\r\n\r\n?></div></div>','','0','20060120110157','','','0','0');
